const mongoose = require("mongoose");

const guildMember = new mongoose.Schema({
    memberID: {
        type: String,
        default: null,
    },
    guildID: {
        type: String,
        default: null,
    },
    xp: {
        type: Number,
        default: 0,
    },
    level: {
        type: Number,
        default: 0,
    },
    money: {
        type: Number,
        default: null,
    },
    bank: {
        type: Number,
        default: null,
    },
    genre: {
        type: String,
        default: null,
    }
});

module.exports = mongoose.model('guildMemberData', guildMember);